function [fitnessHistory, yHistory, generationCount] = OnePlusOneES(targetFunction, yParent, sigma, maxGenerationCount)  generationCount = 0;    dimensions = size(yParent, 1);    % TODO parameter  fitnessParent = targetFunction(yParent);    yHistory = [];  fitnessHistory = [];    do    yHistory = [yHistory ; yParent];    fitnessHistory = [fitnessHistory ; fitnessParent];    mutation = randn(dimensions, 1); % normal distributed random number    yOffspring = yParent + sigma * mutation;    fitnessOffspring = targetFunction(yOffspring);    if fitnessOffspring < fitnessParent      yParent = yOffspring;      fitnessParent = fitnessOffspring;    end    generationCount = generationCount + 1;  until generationCount == maxGenerationCount;  yHistory = [yHistory ; yParent];  fitnessHistory = [fitnessHistory ; fitnessParent];end