% Aufgabe 5 %%%%%%%%%%%%%%yParent = ones(30, 1);sigmaParent = 1;sigmaStop = 10^(-5);offspringPopulationSize = 10;parentPopulationSize = 1;tau = (2*30)^(-1/2);set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) figure("1");[fitnessHistory, sigmaHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Sphere');semilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 6 - Recombination - (1/1, 10), N=30");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("2");parentPopulationSize = 3;[fitnessHistory, sigmaHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Sphere');semilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 6 - Recombination - (3/3, 10), N=30");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("3");yParent = ones(100, 1);sigmaParent = 1;sigmaStop = 10^(-5);offspringPopulationSize = 10;parentPopulationSize = 1;tau = (2*100)^(-1/2);[fitnessHistory, sigmaHistory, fitnessParent, generationCount] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Sphere');fitnessParentgenerationCountsemilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 6 - Recombination - (1/1, 10), N=100");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("4");parentPopulationSize = 3;[fitnessHistory, sigmaHistory, fitnessParent, generationCount] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Sphere');fitnessParentgenerationCountsemilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 6 - Recombination - (3/3, 10), N=100");ylabel("Fitness / Sigma Value");xlabel("Generation number");