% Aufgabe 11 %%%%%%%%%%%%%%%yParent = ones(30, 1) * 10;sigmaParent = 1;sigmaStop = 10^(-5);offspringPopulationSize = 9;parentPopulationSize = 3;tau = (2*30)^(-1/2);maxGenerations = 1000000;set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) figure("1");[fitnessHistory, sigmaHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Rastrigin', maxGenerations);semilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 11 - Recombination - (3/3, 9), N=30");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("2");offspringPopulationSize = 30;parentPopulationSize = 10;[fitnessHistory, sigmaHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Rastrigin', maxGenerations);semilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 11 - Recombination - (10/10, 30), N=30");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("3");offspringPopulationSize = 150;parentPopulationSize = 50;[fitnessHistory, sigmaHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Rastrigin', maxGenerations);semilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 11 - Recombination - (50/50, 150), N=30");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("4");offspringPopulationSize = 300;parentPopulationSize = 100;[fitnessHistory, sigmaHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Rastrigin', maxGenerations);semilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 11 - Recombination - (100/100, 300), N=30");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("5");offspringPopulationSize = 600;parentPopulationSize = 200;[fitnessHistory, sigmaHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Rastrigin', maxGenerations);semilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 11 - Recombination - (200/200, 600), N=30");ylabel("Fitness / Sigma Value");xlabel("Generation number");