% Aufgabe 12 %%%%%%%%%%%%%%%global LensParms; % physical parameters of the geometrical systemLensParms.h = 1;LensParms.b = 20;LensParms.R = 7;LensParms.eps = 1.5;LensParms.x_init = 3; % initial lens geometry (being rectangular)global Weighting; % scalarization factor for bi-objective problemWeighting =.9;n = 15; % number of free geometry parameters to be optimizedmu = 5;lambda = 20;   sigma = 1;sigma_stop = 1e-6;    sa_maxGens = 1000;[cmsa.x, cmsa.fHistory, cmsa.sigmaHistory] = CMSA_ES(mu, lambda, n, sigma, ...    sigma_stop);[sa.fHistory, sa.sigmaHistory, ~, ~] = RecombinationSelfAdaptionES(...    LensParms.x_init*ones(n,1), sigma, sigma_stop, lambda, mu, 1/sqrt(2*n), ...    'LensFunction', sa_maxGens);hold on;figure(1);semilogy(cmsa.fHistory);semilogy(cmsa.sigmaHistory);semilogy(sa.fHistory);semilogy(sa.sigmaHistory);legend("CMSA-EA Fitness History", "CMSA-EA Sigma History", "SA-EA Fitness History", "SA-EA Sigma History");hold off;