% Aufgabe 8 %%%%%%%%%%%%%%yParent = ones(100, 1);sigmaParent = 10^(-2);sigmaStop = 10^(-5);offspringPopulationSize = 10;parentPopulationSize = 3;maxGenerations = 4000;set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) figure("1");tau = 0.025;[fitnessHistory, sigmaHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Sphere', maxGenerations);semilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 8 - Recombination - (3/3, 10), N=100, \tau = 0.25");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("2");tau = 0.035;[fitnessHistory, sigmaHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Sphere', maxGenerations);semilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 8 - Recombination - (3/3, 10), N=100, tau = 0.35");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("3");tau = 0.045;[fitnessHistory, sigmaHistory, fitnessParent, generationCount] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Sphere', maxGenerations);fitnessParentgenerationCountsemilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 8 - Recombination - (3/3, 10), N=100, tau = 0.45");ylabel("Fitness / Sigma Value");xlabel("Generation number");% tau = 0 doesnt work, never endsfigure("4");tau = 0;[fitnessHistory, sigmaHistory, fitnessParent, generationCount] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Sphere', maxGenerations);fitnessParentgenerationCountsemilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 8 - Recombination - (3/3, 10), N=100, tau = 0.00");ylabel("Fitness / Sigma Value");xlabel("Generation number");