% Aufgabe 8 %%%%%%%%%%%%%%dimensions = 100;yParent = ones(dimensions, 1);sigmaParent = 10^(-2);sigmaStop = 10^(-5);offspringPopulationSize = 10;parentPopulationSize = 3;maxGenerations = 4000;tau = 1/sqrt(2*dimensions);set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) figure("1");alpha = 0;[fitnessHistory, sigmaHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, alpha*tau, 'Sphere', maxGenerations);semilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 8 - Recombination - (3/3, 10), N=100, \tau * 0");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("2");alpha = 1;[fitnessHistory, sigmaHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, alpha*tau, 'Sphere', maxGenerations);semilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 8 - Recombination - (3/3, 10), N=100, \tau * 1");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("3");alpha = 10;[fitnessHistory, sigmaHistory, fitnessParent, generationCount] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, alpha*tau, 'Sphere', maxGenerations);fitnessParentgenerationCountsemilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 8 - Recombination - (3/3, 10), N=100, \tau * 10");ylabel("Fitness / Sigma Value");xlabel("Generation number");# zu groÃŸe Schrittweite (sigma = 1) -> "springt" am Optimum vorbeifigure("4");alpha = 0;sigmaParent = 1;[fitnessHistory, sigmaHistory, fitnessParent, generationCount] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, alpha*tau, 'Sphere', maxGenerations);fitnessParentgenerationCountsemilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 8 - Recombination - (3/3, 10), N=100, \tau * 10");ylabel("Fitness / Sigma Value");xlabel("Generation number");