% Aufgabe 9 %%%%%%%%%%%%%%yParent = ones(100, 1);sigmaParent = 1;sigmaStop = 10^(-5);offspringPopulationSize = 10;parentPopulationSize = 3;tau = (2*100)^(-1/2);maxGenerations = 1000000;set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) figure("1");[fitnessHistory, sigmaHistory, fitnessParent, generationCount, functionEvaluationCount] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Sphere', maxGenerations);fitnessParentgenerationCountfunctionEvaluationCountsemilogy(fitnessHistory,'b-');hold onsemilogy(sigmaHistory,'r-');legend("Fitness History","Sigma History");title("Exercise 9 - Recombination - (3/3, 10), N=100");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("2");yStart = ones(100, 1);sigma = 1;sigmaStop = 10^(-5);[fitnessHistoryOneFifth, fitnessParentOneFifth, generationCountOneFifth, sigmaHistoryOneFifth, yParentOneFifth, functionEvaluationCountOneFifth] = OnePlusOneESOneFifth("Sphere", [], yStart, sigma, sigmaStop);fitnessParentOneFifthgenerationCountOneFifthfunctionEvaluationCountOneFifthsemilogy(fitnessHistoryOneFifth,'b-');hold onsemilogy(sigmaHistoryOneFifth,'r-');legend("Fitness History","Sigma History");title("Exercise 9 - 1+1 ES 1/5, N=100");ylabel("Fitness / Sigma Value");xlabel("Generation number");