% rosenbrock function% author : Daniel Griesser function [f,g] = Rosenbrock(x)  % input:  % x - vector in column form   % output:  % f - function value [1x1]   % g - gradient vector [nx1]    % check input  n = size(x, 1);  if n == 1    fprintf('input is row vector, transform it into column vector \n')    x = x';     n = size(x, 1);  endif    % function value  f = 0;  for i = 1:(n-1)    f = f + (100*(x(i)^2-x(i+1))^2 + (x(i)-1)^2);  endfor    % gradient vector   g = zeros(n, 1);  for i = 1:n    if i > 1      g(i) += -200 * (x(i-1) ** 2 - x(i));    endif    if i < n      g(i) += 200 * 2 * x(i) * (x(i) ** 2 - x(i+1)) + 2 * (x(i) - 1);    endif  endfor  endfunction