% Aufgabe 2 %%%%%%%%%%%%%%set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) dimensions = 40;yParent = ones(dimensions, 1);sigmaParent = 1;sigmaStop = 10^(-5);offspringPopulationSize = 10;parentPopulationSize = 3;searchPath = ones(dimensions, 1);searchPathConstant = 1/sqrt(dimensions);attenuation = sqrt(dimensions);maxGenerations = 2000;desiredFitness = 0;%10^(-10);tau = (2*dimensions)^(-1/2); % durch diesen Wert von Tau ist die SA nahezu optimal (am Kugelmodell)[fitnessHistorySA, sigmaHistorySA, fitnessParent, generationCount, functionEvaluationCount, fitnessHistoryFunctionEvaluation, sigmaNormHistorySA, yHistory] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, 'Sphere', maxGenerations, desiredFitness);[fitnessHistoryCSA, sigmaHistoryCSA, fitnessParent, generationCount, functionEvaluationCount, fitnessHistoryFunctionEvaluation, sigmaNormHistoryCSA, yHistory] = CSAES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, 'Sphere', searchPath, searchPathConstant, attenuation, maxGenerations);figure("1");semilogy(fitnessHistoryCSA,'b-');hold onsemilogy(fitnessHistorySA,'r-');hold onsemilogy(sigmaHistoryCSA,'m-');hold onsemilogy(sigmaHistorySA,'c-');legend("CSA Fitness", "SA Fitness","CSA Sigma", "SA Sigma");title("Exercise 2 - CSA-ES - Fitness Dynamics - (3/3, 10), N=40");ylabel("Fitness / Sigma Value");xlabel("Generation number");% ersichtlich: SA performanter am Kugelmodellfigure("2");semilogy(sigmaNormHistoryCSA,'b-');hold onsemilogy(sigmaNormHistorySA,'r-');legend("CSA", "SA");title("Exercise 2 - CSA-ES - Normalized Mutation Strength - (3/3, 10), N=40");ylabel("Normalized Mutation Strength");xlabel("Generation number");