% Aufgabe 5 %%%%%%%%%%%%%%set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) dimensions = 40;yParent = ones(dimensions, 1);sigmaParent = 1;sigmaStop = 10^(-5);offspringPopulationSize = 10;parentPopulationSize = 3;searchPath = ones(dimensions, 1);searchPathConstant = 1/sqrt(dimensions);attenuation = sqrt(dimensions);maxGenerations = 2000;desiredFitness = 0;%10^(-10);tau = (2*dimensions)^(-1/2);[fitnessHistoryCSA, sigmaHistoryCSA, fitnessParent, generationCount, functionEvaluationCount, fitnessHistoryFunctionEvaluation, sigmaNormHistoryCSA, yHistory] = CSAES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, 'Sphere', searchPath, searchPathConstant, attenuation, maxGenerations);evolutionPath = ones(dimensions, 1);evolutionPathConstant = 1/sqrt(dimensions);covarianceConstant = 2/(dimensions + sqrt(2))^2;covarianceMatrix = eye(dimensions, dimensions);[fitnessHistoryCMA, sigmaHistoryCMA, fitnessParent, generationCount, functionEvaluationCount, fitnessHistoryFunctionEvaluation, sigmaNormHistoryCMA, yHistory] = CMAES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, 'Sphere', searchPath, searchPathConstant, attenuation, maxGenerations, covarianceConstant, evolutionPathConstant, evolutionPath, covarianceMatrix);figure("1");semilogy(fitnessHistoryCSA,'b-');hold onsemilogy(fitnessHistoryCMA,'r-');hold onsemilogy(sigmaHistoryCSA,'m-');hold onsemilogy(sigmaHistoryCMA,'c-');legend("CSA Fitness", "CMA Fitness","CSA Sigma", "CMA Sigma");title("Exercise 5 - CSA-ES - CMA-ES - Fitness Dynamics - (3/3, 10), N=40");ylabel("Fitness / Sigma Value");xlabel("Generation number");figure("2");semilogy(sigmaNormHistoryCSA,'b-');hold onsemilogy(sigmaNormHistoryCMA,'r-');legend("CSA", "CMA");title("Exercise 5 - CSA-ES - CMA-ES - Normalized Mutation Strength - (3/3, 10), N=40");ylabel("Normalized Mutation Strength");xlabel("Generation number");