% Aufgabe 6 %%%%%%%%%%%%%%set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) dimensions = 30;yParent = ones(dimensions, 1);sigmaParent = 1;sigmaStop = 10^(-5);offspringPopulationSize = 10;parentPopulationSize = 3;searchPath = ones(dimensions, 1);searchPathConstant = 1/sqrt(dimensions);attenuation = sqrt(dimensions);maxGenerations = 2000;desiredFitness = 0;%10^(-10);tau = (2*dimensions)^(-1/2);evolutionPath = ones(dimensions, 1);evolutionPathConstant = 1/sqrt(dimensions);covarianceConstant = 2/(dimensions + sqrt(2))^2;covarianceMatrix = eye(dimensions, dimensions);functions = {'Cigar';'CigarTablet';'Ellipsoid2';'ParabolicRidge';'SharpRidge';'DifferentPowers'};for f = 1:size(functions)  functionName = functions{f}  [fitnessHistoryCSA, sigmaHistoryCSA, fitnessParent, generationCount, functionEvaluationCount, fitnessHistoryFunctionEvaluation, sigmaNormHistoryCSA, yHistory] = CSAES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, functionName, searchPath, searchPathConstant, attenuation, maxGenerations);  [statisticsCMA, fitnessParentCMA] = CMAES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, functionName, searchPath, searchPathConstant, attenuation, maxGenerations, covarianceConstant, evolutionPathConstant, evolutionPath, covarianceMatrix);  fitnessHistoryCMA = statisticsCMA.fitnessHistory;  sigmaHistoryCMA = statisticsCMA.sigmaHistory;  [fitnessHistorySA, sigmaHistorySA, fitnessParent, generationCount, functionEvaluationCount, fitnessHistoryFunctionEvaluation, sigmaNormHistorySA, y2HistorySA] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, functionName, maxGenerations, desiredFitness);  plot = figure(f);  semilogy(fitnessHistoryCSA,'b-');  hold on  semilogy(fitnessHistoryCMA,'r-');  hold on  semilogy(fitnessHistorySA,'g-');  hold on  semilogy(sigmaHistoryCSA,'m-');  hold on  semilogy(sigmaHistoryCMA,'c-');  hold on  semilogy(sigmaHistorySA,'y-');  legend("CSA Fitness", "CMA Fitness", "SA Fitness","CSA Sigma", "CMA Sigma", "SA Sigma");  title(strcat("Exercise 6 - CSA-ES - CMA-ES - SA-ES - Fitness Dynamics - ", functionName));  ylabel("Fitness / Sigma Value");  xlabel("Generation number");    print(plot, strcat("Exercise 6 - CSA-ES - CMA-ES - SA-ES - Fitness Dynamics - ", functionName, ".png"), "-dpng");end