% Aufgabe 7 %%%%%%%%%%%%%%set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) dimensions = 30;yParent = ones(dimensions, 1);sigmaParent = 1;sigmaStop = 10^(-10);offspringPopulationSize = 10;parentPopulationSize = 3;searchPath = ones(dimensions, 1);searchPathConstant = 1/sqrt(dimensions);attenuation = sqrt(dimensions);maxGenerations = 5000;desiredFitness = 0;%10^(-10);tau = (2*dimensions)^(-1/2);evolutionPath = ones(dimensions, 1);evolutionPathConstant = 1/sqrt(dimensions);covarianceConstant = 2/(dimensions + sqrt(2))^2;covarianceMatrix = eye(dimensions, dimensions);functions = {'Cigar';'Tablet';'Ellipsoid2';'ParabolicRidge';'SharpRidge';'DifferentPowers'};for f = 1:size(functions)  functionName = functions{f}  [statisticsCMA, fitnessParentCMA] = CMAES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, functionName, searchPath, searchPathConstant, attenuation, maxGenerations, covarianceConstant, evolutionPathConstant, evolutionPath, covarianceMatrix);  plot = figure(f);  semilogy(statisticsCMA.sigmaHistory,'b-');  hold on  semilogy(statisticsCMA.maxEigValues,'r-');  hold on  semilogy(statisticsCMA.minEigValues,'g-');  hold on  semilogy(statisticsCMA.relationMaxMinEigValues,'c-');  if(strncmp(functionName,"DifferentPowers",5) || strncmp(functionName,"ParabolicRidge",5))    legendPosition = "northwest";  else    legendPosition = "southwest";  end  legend("Sigma", "max(gamma)", "min(gamma)", "max(gamma)/min(gamma)", "location", legendPosition);  title(strcat("Exercise 7 - CMA-ES - Sigma Dynamics and C Matrix Eigenwert - ", functionName));  ylabel("Fitness / Sigma Value");  xlabel("Generation number");    print(plot, strcat("Exercise 7 - CMA-ES - Sigma Dynamics and C Matrix Eigenwert - ", functionName, ".png"), "-dpng");end