% Aufgabe 9 %%%%%%%%%%%%%%set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) dimensions = 30;yParent = ones(dimensions, 1);sigmaParent = 1;sigmaStop = 10^(-10);offspringPopulationSize = 120;parentPopulationSize = 30;searchPath = ones(dimensions, 1);searchPathConstant = 1/sqrt(dimensions);attenuation = sqrt(dimensions);maxGenerations = 5000;desiredFitness = 0;%10^(-10);tau = (2*dimensions)^(-1/2);evolutionPath = ones(dimensions, 1);evolutionPathConstant = 1/sqrt(dimensions);covarianceConstant = 2/(dimensions + sqrt(2))^2;covarianceMatrix = eye(dimensions, dimensions);functions = {'Cigar';'Tablet';'Ellipsoid2';'ParabolicRidge';'SharpRidge';'DifferentPowers'};for f = 1:size(functions)  functionName = functions{f}  [fitnessHistoryCMSA, sigmaHistoryCMSA, fitnessParent, generationCount, functionEvaluationCount, fitnessHistoryFunctionEvaluation, sigmaNormHistoryCMSA, yHistory] = CMSAES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, functionName, searchPath, searchPathConstant, attenuation, maxGenerations, covarianceConstant, evolutionPathConstant, evolutionPath, covarianceMatrix);  [statisticsCMA, fitnessParent] = CMAES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, functionName, searchPath, searchPathConstant, attenuation, maxGenerations, covarianceConstant, evolutionPathConstant, evolutionPath, covarianceMatrix);  fitnessHistoryCMA = statisticsCMA.fitnessHistory;  sigmaHistoryCMA = statisticsCMA.sigmaHistory;  [fitnessHistoryCMSARankMu, sigmaHistoryCMSARankMu, fitnessParent, generationCount, functionEvaluationCount, fitnessHistoryFunctionEvaluation, sigmaNormHistoryCMSARankMu, yHistory] = CMSAESRankMu(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, functionName, searchPath, searchPathConstant, attenuation, maxGenerations, covarianceConstant, evolutionPathConstant, evolutionPath, covarianceMatrix);    plot = figure(f);  semilogy(fitnessHistoryCMSA,'b-');  hold on  semilogy(fitnessHistoryCMA,'r-');  hold on  semilogy(fitnessHistoryCMSARankMu,'g-');  hold on  semilogy(sigmaHistoryCMSA,'m-');  hold on  semilogy(sigmaHistoryCMA,'c-');  hold on  semilogy(sigmaHistoryCMSARankMu,'y-');  legend("CMSA Fitness", "CMA Fitness", "CMSA Rank \\mu Fitness", "CMSA Sigma", "CMA Sigma", "CMSA Rank \\mu Sigma");  title(strcat("Exercise 9 - CMSA-Rank\\mu-ES - Fitness / Sigma Dynamics - (30/30,120) - ", functionName));  ylabel("Fitness / Sigma Value");  xlabel("Generation number");    print(plot, strcat("Exercise 9 - CMSA-RankMu-ES - Fitness + Sigma Dynamics - (30 30,120) - ", functionName, ".png"), "-dpng");end