% Exercise 1 %%%%%%%%%%%%%%%set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) functions = {4,8,16,32,64};for f = 1:size(functions)  functionName = functions{f}  [fitnessHistoryCMSA, sigmaHistoryCMSA, fitnessParent, generationCount, functionEvaluationCount, fitnessHistoryFunctionEvaluation, sigmaNormHistoryCMSA, yHistory, yParent] = CMSAES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, functionName, searchPath, searchPathConstant, attenuation, maxGenerations, covarianceConstant, evolutionPathConstant, evolutionPath, covarianceMatrix);  yParent  fitnessParent    plot = figure(f);  semilogy(fitnessHistoryCMSA,'r-');  hold on  semilogy(sigmaHistoryCMSA,'b-');  legend("CMSA Fitness", "CMSA Sigma");  title(strcat("Exercise 12 - CMSA-ES - Fitness Dynamics - ", functionName));  ylabel("Fitness / Sigma Value");  xlabel("Generation number");    print(plot, strcat("Exercise 12 - CMSA-ES - Fitness Dynamics - ", functionName, ".png"), "-dpng");end