% Exercise 1 %%%%%%%%%%%%%%%clear all;more off;set (0, "defaultaxesfontname", "Helvetica")set (0, "defaultaxesfontsize", 14)set (0, "defaulttextfontname", "Helvetica")set (0, "defaulttextfontsize", 14) dimensions = {4;8;16;32;64};sigmaParent = 1;sigmaStop = 0;offspringPopulationSize = 12;parentPopulationSize = 3;maxGenerations = 20000;desiredFitness = 10^(-4);runs = 15;ERTHistorySphere = [];for f = 1:size(dimensions)  ERTTotalEvaluationsSphere = 0;  runsToSucc = 0;  for r = 1:runs     dimension = dimensions{f};    yParent = ones(dimension,1)*10;    tau = (2*dimension)^(-1/2);    functionName = 'Sphere';    printf("%s %d\n", functionName, dimension);        [yParent, fitnessParent, stats] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, functionName, maxGenerations, desiredFitness);        ERTTotalEvaluationsSphere += stats.functionEvaluationCount;    if(fitnessParent < desiredFitness)      runsToSucc++;    end  end  ERTHistorySphere = [ERTHistorySphere ; (ERTTotalEvaluationsSphere / runsToSucc)];endERTHistoryEllipsoid1 = [];for f = 1:size(dimensions)  ERTTotalEvaluationsEllipsoid1 = 0;  runsToSucc = 0;  for r = 1:runs    dimension = dimensions{f};    yParent = ones(dimension,1)*10;    tau = (2*dimension)^(-1/2);    functionName = 'Ellipsoid1';    printf("%s %d\n", functionName, dimension);        [yParent, fitnessParent, stats] = RecombinationSelfAdaptionES(yParent, sigmaParent, sigmaStop, offspringPopulationSize, parentPopulationSize, tau, functionName, maxGenerations, desiredFitness);    ERTTotalEvaluationsEllipsoid1 += stats.functionEvaluationCount;    if(fitnessParent < desiredFitness)      runsToSucc++;    end  end  ERTHistoryEllipsoid1 = [ERTHistoryEllipsoid1 ; (ERTTotalEvaluationsEllipsoid1 / runsToSucc)];endplot1 = figure(1);dimensions = [4;8;16;32;64];loglog(dimensions, ERTHistorySphere, 'r-');hold onloglog(dimensions, ERTHistoryEllipsoid1, 'b-');legend("ERT Sphere", "ERT Ellipsoid1");title(strcat("Exercise 1 - ERT"));ylabel("ERT(N)");xlabel("N");  print(plot1, strcat("Exercise 1 - ERT", ".png"), "-dpng");