clearinputMatrixOld = load("K100M2-NN.dat");np = 50;fnr = fopen("K100M2-NN.dat","r");inputMatrix = fscanf(fnr, "%f", [2, 2*np])';fclose(fnr);inputMatrixOld(43,:)inputMatrix(43,:)dk = [(ones(50,1)*(-1));ones(50,1)];maxIterations = 1500;alpha = 0.001;[weightMatrix, weightMatrixChanges, errorHistory, iteration, weightMatrixFound, weightMatrixHistory] = AlphaLMS(inputMatrix, dk, alpha, maxIterations);weightMatrixFoundweightMatrixxLowerBound = min(inputMatrix(:,1));xUpperBound = max(inputMatrix(:,1));yLowerBound = min(inputMatrix(:,2));yUpperBound = max(inputMatrix(:,2));figure 1axis([xLowerBound-abs(0.1*(xUpperBound-xLowerBound)),xUpperBound+abs(0.1*(xUpperBound-xLowerBound)),yLowerBound-abs(0.1*(yUpperBound-yLowerBound)),yUpperBound+abs(0.1*(yUpperBound-yLowerBound))]);plot( inputMatrix(1:50,1), inputMatrix(1:50,2), 'x',       inputMatrix(51:100,1), inputMatrix(51:100,2), 'o',      [xLowerBound, xUpperBound], [-(xLowerBound*weightMatrix(1)+weightMatrix(3))/weightMatrix(2), -(xUpperBound*weightMatrix(1)+weightMatrix(3))/weightMatrix(2)], "1");figure 2semilogy(errorHistory);